import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import Chart from "chart.js/auto";
import "./App.css";

// EKG Simulation Helper Functions

function gaussianWave(x, mean, amplitude, width) {
    return amplitude * Math.exp(-((x - mean) ** 2) / (2 * width ** 2));
}

function ekgWaveform(t, heartRate = 60, pWave = true) {
    const beatDuration = 60 / heartRate;
    let ekgBeat = Array(t.length).fill(0);

    // Define wave positions
    const pWaveStart = pWave ? 0.2 * beatDuration : null;
    const qWaveStart = 0.4 * beatDuration;
    const rWaveStart = 0.45 * beatDuration;
    const sWaveStart = 0.5 * beatDuration;
    const tWaveStart = 0.7 * beatDuration;

    // Add P wave
    if (pWaveStart !== null) {
        ekgBeat = ekgBeat.map((y, i) => y + gaussianWave(t[i], pWaveStart, 0.1, 0.05 * beatDuration));
    }

    // QRS complex and T wave
    ekgBeat = ekgBeat.map((y, i) =>
        y -
        gaussianWave(t[i], qWaveStart, 0.15, 0.02 * beatDuration) +
        gaussianWave(t[i], rWaveStart, 1.0, 0.01 * beatDuration) -
        gaussianWave(t[i], sWaveStart, 0.2, 0.02 * beatDuration) +
        gaussianWave(t[i], tWaveStart, 0.3, 0.1 * beatDuration)
    );

    return ekgBeat;
}

function generateEKG(duration = 5, heartRate = 72, samplingRate = 1000, numWaves = 1) {
    const numSamples = duration * samplingRate;
    const t = Array.from({ length: numSamples }, (_, i) => i / samplingRate);
    let ekgSignal = Array(t.length).fill(0);

    for (let i = 0; i < numWaves; i++) {
        const start = Math.floor(i * (60 / heartRate) * samplingRate);
        const end = start + Math.floor((60 / heartRate) * samplingRate);
        const ekgBeat = ekgWaveform(t.slice(0, end - start), heartRate);

        for (let j = start; j < end && j < ekgSignal.length; j++) {
            ekgSignal[j] += ekgBeat[j - start] + 0.05 * (Math.random() - 0.5); // Add noise
        }
    }

    return { t, ekgSignal };
}

// Arrhythmias data structure
const arrhythmias = {
    "Sinus Tachycardia": { heartRate: 100, description: "Sinus tachycardia is characterized by a regular rhythm with a P wave preceding every QRS complex." },
    "Afib with RVR": { heartRate: 150, description: "Atrial fibrillation with rapid ventricular response lacks distinct P waves and has variable R-R intervals." },
    "AVRT": { heartRate: 220, description: "AVRT shows a very fast rate (200-300 bpm), with retrograde P waves and variable R wave amplitude." },
    "AVNRT": { heartRate: 180, description: "AVNRT has a fast rate, with retrograde P waves possibly overlaying the QRS." }
};

function EKGSimulator() {
    const [chartData, setChartData] = useState(null);
    const [userDiagnosis, setUserDiagnosis] = useState("");
    const [chosenEKGType, setChosenEKGType] = useState("");
    const [numPulses, setNumPulses] = useState(3); // Default to 3 pulses

    const handleDiagnosisChange = (event) => {
        setUserDiagnosis(event.target.value);
    };

    const handleNumPulsesChange = (event) => {
        const value = parseInt(event.target.value);
        if (value === 3 || value === 5) {
            setNumPulses(value);
        }
    };

    const checkDiagnosis = () => {
        if (userDiagnosis.toLowerCase() === chosenEKGType.toLowerCase()) {
            alert("Correct! Well done!");
        } else {
            alert(`Incorrect. The correct answer was '${chosenEKGType}'.`);
        }
        // Show explanation
        alert(arrhythmias[chosenEKGType].description);
    };

    const simulateRandomEKG = () => {
        const arrhythmiaTypes = Object.keys(arrhythmias);
        const randomType = arrhythmiaTypes[Math.floor(Math.random() * arrhythmiaTypes.length)];
        setChosenEKGType(randomType);

        const { heartRate } = arrhythmias[randomType];
        const { t, ekgSignal } = generateEKG(5, heartRate, 1000, numPulses); // Generate the selected number of pulses

        const data = {
            labels: t,
            datasets: [{
                label: `EKG Signal - ${randomType}`,
                data: ekgSignal,
                borderColor: "black", // Change the line color to black
                pointRadius: 0, // Remove bullet points
                showLine: true,  // Ensure line is shown
                fill: false
            }],
        };

        setChartData(data);
    };

    useEffect(() => {
        simulateRandomEKG();
    }, [numPulses]); // Re-run simulation when numPulses changes

    return (
        <div className="App">
            <h1>EKG Simulator</h1>
            <div>
                <input
                    type="text"
                    value={userDiagnosis}
                    onChange={handleDiagnosisChange}
                    placeholder="Enter your diagnosis"
                />
                <button onClick={checkDiagnosis}>Submit Diagnosis</button>
                <br />
                <label>
                    Number of pulses:
                    <select value={numPulses} onChange={handleNumPulsesChange}>
                        <option value={3}>3</option>
                        <option value={5}>5</option>
                    </select>
                </label>
                <button onClick={simulateRandomEKG}>Generate EKG</button>
            </div>
            <div className="chart-container">
                {chartData && (
                    <div className="chart">
                        <Line data={chartData} options={{
                            responsive: true,
                            maintainAspectRatio: false,
                            scales: {
                                x: {
                                    title: {
                                        display: true,
                                        text: 'Time (s)',
                                    },
                                },
                                y: {
                                    title: {
                                        display: true,
                                        text: 'Voltage (mV)',
                                    },
                                    beginAtZero: true,
                                }
                            },
                        }} />
                    </div>
                )}
            </div>
        </div>
    );
}a

export default EKGSimulator;
