import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import Chart from "chart.js/auto";
import "./App.css";

// EKG Simulation Helper Functions in JavaScript

function gaussianWave(x, mean, amplitude, width) {
    return amplitude * Math.exp(-((x - mean) ** 2) / (2 * width ** 2));
}

function ekgWaveform(t, heartRate = 60, pWave = true, constantRR = true, retrogradeP = false) {
    const beatDuration = 60 / heartRate;
    let ekgBeat = Array(t.length).fill(0);
    const peaks = {};

    // Define wave positions
    const pWaveStart = pWave ? 0.2 * beatDuration : null;
    const qWaveStart = 0.4 * beatDuration;
    const rWaveStart = 0.45 * beatDuration;
    const sWaveStart = 0.5 * beatDuration;
    const tWaveStart = 0.7 * beatDuration;

    // Add P wave
    if (pWaveStart !== null) {
        ekgBeat = ekgBeat.map((y, i) => y + gaussianWave(t[i], pWaveStart, 0.1, 0.05 * beatDuration));
    }

    // QRS complex and T wave
    ekgBeat = ekgBeat.map((y, i) =>
        y -
        gaussianWave(t[i], qWaveStart, 0.15, 0.02 * beatDuration) +
        gaussianWave(t[i], rWaveStart, 1.0, 0.01 * beatDuration) -
        gaussianWave(t[i], sWaveStart, 0.2, 0.02 * beatDuration) +
        gaussianWave(t[i], tWaveStart, 0.3, 0.1 * beatDuration)
    );

    // Add variability if necessary
    if (!constantRR) {
        const randomFactor = Math.random() * 0.4 + 0.8; // Random between 0.8 and 1.2
        beatDuration *= randomFactor;
    }

    // Retrograde P waves
    if (retrogradeP) {
        const retrogradePosition = rWaveStart - 0.15 * beatDuration;
        ekgBeat = ekgBeat.map((y, i) => y + gaussianWave(t[i], retrogradePosition, -0.08, 0.04 * beatDuration));
    }

    // Return peaks
    peaks.P = pWaveStart;
    peaks.Q = qWaveStart;
    peaks.R = rWaveStart;
    peaks.S = sWaveStart;
    peaks.T = tWaveStart;

    return { ekgBeat, peaks };
}

function generateEKG(duration = 5, heartRate = 72, samplingRate = 1000, ...params) {
    const numSamples = duration * samplingRate;
    const t = Array.from({ length: numSamples }, (_, i) => i / samplingRate);
    let ekgSignal = Array(t.length).fill(0);
    const peaksList = [];

    for (let i = 0; i < Math.floor(duration * heartRate / 60); i++) {
        const start = Math.floor(i * 60 / heartRate * samplingRate);
        const end = start + Math.floor(60 / heartRate * samplingRate);
        const { ekgBeat, peaks } = ekgWaveform(
            t.slice(0, end - start),
            heartRate,
            ...params
        );

        // Add noise and combine beats
        for (let j = start; j < end && j < ekgSignal.length; j++) {
            ekgSignal[j] = ekgBeat[j - start] + 0.05 * (Math.random() - 0.5);
        }

        for (let [peak, time] of Object.entries(peaks)) {
            if (time !== null) {
                peaksList.push({ peak, time: t[start] + time });
            }
        }
    }

    return { t, ekgSignal, peaksList };
}

// Arrhythmias data structure
const arrhythmias = {
    "Sinus Tachycardia": { heartRate: 100, pWave: true, constantRR: true, description: "Sinus tachycardia is characterized by a regular rhythm with a P wave preceding every QRS complex." },
    "Afib with RVR": { heartRate: 150, pWave: false, constantRR: false, description: "Atrial fibrillation with rapid ventricular response lacks distinct P waves and has variable R-R intervals." },
    "AVRT": { heartRate: 220, retrogradeP: true, constantRR: false, description: "AVRT shows a very fast rate (200-300 bpm), with retrograde P waves and variable R wave amplitude." },
    "AVNRT": { heartRate: 180, retrogradeP: true, pWave: false, constantRR: true, description: "AVNRT has a fast rate, with retrograde P waves possibly overlaying the QRS." }
};

function EKGSimulator() {
    const [chartData, setChartData] = useState(null);
    const [userDiagnosis, setUserDiagnosis] = useState("");
    const [chosenEKGType, setChosenEKGType] = useState("");

    const handleDiagnosisChange = (event) => {
        setUserDiagnosis(event.target.value);
    };

    const checkDiagnosis = () => {
        if (userDiagnosis.toLowerCase() === chosenEKGType.toLowerCase()) {
            alert("Correct! Well done!");
        } else {
            alert(`Incorrect. The correct answer was '${chosenEKGType}'.`);
        }
        // Show explanation
        alert(arrhythmias[chosenEKGType].description);
    };

    const simulateRandomEKG = () => {
        const arrhythmiaTypes = Object.keys(arrhythmias);
        const randomType = arrhythmiaTypes[Math.floor(Math.random() * arrhythmiaTypes.length)];
        setChosenEKGType(randomType);

        const { heartRate, ...params } = arrhythmias[randomType];
        const { t, ekgSignal } = generateEKG(5, heartRate, 1000, params);
        setChartData({
            labels: t,
            datasets: [{
                label: "EKG Signal",
                data: ekgSignal,
                borderColor: "blue",
                fill: false
            }]
        });
    };

    useEffect(() => {
        simulateRandomEKG();
    }, []);

    return (
        <div className="App">
            <h1>EKG Simulator</h1>
            <div>
                <input
                    type="text"
                    value={userDiagnosis}
                    onChange={handleDiagnosisChange}
                    placeholder="Enter your diagnosis"
                />
                <button onClick={checkDiagnosis}>Submit Diagnosis</button>
                <button onClick={simulateRandomEKG}>Next EKG</button>
            </div>
            {chartData && <Line data={chartData} />}
        </div>
    );
}

export default EKGSimulator;
